+  width: 25%;
+  text-align: center;
+  color: #000;
+  transition: transform 0.3s;
+
+  h3 {
+    font-size: 1.5rem;
+    margin-bottom: 20px;
+  }
+
+  p {
+    font-size: 1rem;
+    color: #555;
+  }
+
+  &:hover {
+    transform: translateY(-5px); /* Efeito de levantar ao passar o mouse */
+  }
+
+  @media (max-width: 768px) {
+    width: 80%; /* Ajusta a largura para dispositivos menores */
+    margin-bottom: 30px;
+  }
+`;
