+
+  button {
+    background-color: #f53629;
+    width: 230px;
+    color: white;
+    padding: 10px 20px;
+    font-size: 1rem;
+    border: none;
+    border-radius: 5px;
+    cursor: pointer;
+    transition: all 0.3s ease;
+
+    &:hover {
+      background-color: #d72f23;
+      transform: translateY(-2px);
+    }
+  }
+`;
+
+export const Footer = styled.footer`
+  display: flex;
+  width: 100%;
+  height: 200px;
+  background-color: #333;
+  color: white;
+  flex-direction: column;
+  justify-content: center;
+  align-items: center;
+  padding: 20px 0;
+
+  p {
+    margin: 2px;
+    font-size: 0.9rem;
+  }
+
+  @media (max-width: 768px) {
+    height: 150px;
+  }
+
+  .social-icons {
+    display: flex;
+    justify-content: center;
