+      transition: color 0.3s;
+
+      &:hover {
+        color: #ffcc00;
+      }
+    }
+  }
+`;
+
+export const FleetSection = styled.section`
+  width: 100%;
+  height: auto;
+  background-color: #f5f5f5;
+  text-align: center;
+  min-height: 600px;
+  padding: 50px 0;
+
+  h2 {
+    font-size: 2.5rem;
+    color: #333;
+    margin-bottom: 30px;
+  }
+
+  .fleet-cards {
+    display: flex;
+    justify-content: space-around;
+    margin-top: 20px;
+
+    @media (max-width: 768px) {
+      flex-direction: column;
+      align-items: center;
+      height: auto;
+    }
+  }
+`;
+
+export const FleetCard = styled.div`
+  background: white;
+  border-radius: 7px;
+  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
+  padding: 20px;
+  width: 20%;
+  text-align: center;
+
+  h3 {
+    font-size: 1.5rem;
+    color: #333;
+    margin-bottom: 15px;
+  }
+
+  p {
+    font-size: 1rem;
+    color: #555;
+  }
+
+  @media (max-width: 768px) {
+    width: 80%;
+    margin-bottom: 20px;
+  }
+`;
