+    color: #fff;
+    text-decoration: none;
+    font-weight: bold;
+    position: relative;
+
+    &:hover {
+      color: #f26b2b;
+      transition: color 0.3s ease;
+    }
+
+    &:hover::after {
+      content: '';
+      width: 100%;
+      height: 2px;
+      background-color: #f26b2b;
+      position: absolute;
+      bottom: -5px;
+      left: 0;
+      animation: ${fadeIn} 0.3s ease;
+    }
+  }
+`;
